```c++
// define element type
typedef int ElemType;

// define struct of linked list
typedef struct LNode {
    ElemType data;
    struct LNode *next;
} LNode, *LinkList;

// define Status
typedef enum Status {
    ERROR,
    SUCCESS
} Status;

LinkList CreateList_Head(LinkList &L)
{
    LNode *s;
    int x;
    L = (LinkList)malloc(sizeof(LNode));     //创建头结点
    L->next  =NULL;
    scanf("%d",&x);
    while(x!=9999){
        s=(LNode*)malloc(sizeof(LNode));
        s->data=x;
        s->next=L->next;
        L->next=s;                          //插入结点
        scanf("%d",&x);
    }
    return L;
}

void TraverseList(LinkList L)
{
    LNode *p = L->next;
    while(p){
        cout << p->data << " " ;
        p = p->next;
    }
}

int main(){
    LinkList L;
    L = new LNode;
    CreateList_Head(L);
    TraverseList(L);
    return 0;
}
