class Solution {
public:
    ListNode* ReverseList(ListNode* pHead) {
    if(!pHead || !pHead->next)
        return pHead;
    else{
        ListNode *p = pHead,*r = pHead->next, *q = p;
        while(r->next){
            p->next = r->next;
            r->next = q;
            q = r;
            r = p->next;
        }
        r->next = q;
        p->next = NULL;
        pHead = r;
    }
        return pHead;
    }
};
