class Solution {
public:
    int search(vector<int>& nums, int target) {
        int high = nums.size(), mid = high / 2, left = 0;
        if(!high)  return -1;
        while(left <= high){
            mid = (left + high)/2;
            if(nums[mid] == target)
                return mid;
            else if(nums[mid] < target)
                left = mid + 1;
            else
                high = mid-1;
        }
        return -1;
    }
};
