class Solution {
  public:
    vector<vector<int> > Print(TreeNode* pRoot) {
        vector<vector<int> > res;
        if (pRoot == NULL)   //如果是空，则直接返回空vector
            return res;
        stack<TreeNode*> q, q2;     //队列存储，进行层次遍历
        q.push(pRoot);
        TreeNode* cur;
        int tag = 1;
        while (!q.empty() || !q2.empty()) {
            //记录二叉树的某一行
            vector<int> row;
            int n = tag ? q.size() : q2.size();
            //因先进入的是根节点，故每层节点多少，队列大小就是多少
            for (int i = 0; i < n; i++) {
                cur = (tag) ? q.top() : q2.top();
                if (tag)  q.pop();
                else  q2.pop();
                row.push_back(cur->val);
                //若是左右孩子存在，则存入左右孩子作为下一个层次
                if (tag) {
                    if (cur->left)
                        q2.push(cur->left);
                    if (cur->right)
                        q2.push(cur->right);
                } else {
                    if (cur->right)
                        q.push(cur->right);
                    if (cur->left)
                        q.push(cur->left);
                }
            }
            tag = !tag;
            //每一层加入输出
            res.push_back(row);
        }
        return res;
    }
};
```

